    private void edit8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_edit8ActionPerformed
        jDesktopPane1.removeAll();
        jDesktopPane1.repaint();
       jDesktopPane1.add(master_item_edit);
       master_item_edit.setSize(900,600);
       if(jTable3.getSelectedRow()!=-1)
        {
          String str1[]=key.split("%");
          try{
          key1=str1[jTable3.getSelectedRow()]+"";    
          ResultSet rs=st.executeQuery("select * from item where item_id="+str1[jTable3.getSelectedRow()]+";");
          Connection con=createConnection();
          Statement st1=con.createStatement();
          jComboBox9.removeAllItems();
          jComboBox10.removeAllItems();          
          ResultSet rs1=st1.executeQuery("select * from unit;");
          while(rs1.next())
          {
              jComboBox10.addItem(rs1.getString("unit_name"));
          }
          ResultSet rs2=st1.executeQuery("select * from itemgroup;");
          while(rs2.next())
          {
              jComboBox9.addItem(rs2.getString("itemgroup_name"));
           }
          if(rs.next())
          { 
            ResultSet rs3=st1.executeQuery("select * from itemgroup where itemgroup_id+"+rs.getString("itemgroup_id")+";");  
            if(rs3.next())   
            {
              jComboBox9.setSelectedItem(rs3.getString("itemgroup_name"));
            }
            ResultSet rs4=st1.executeQuery("select * from unit where unit_id+"+rs.getString("unit_id")+";");  
            if(rs4.next())   
            {
              jComboBox10.setSelectedItem(rs4.getString("unit_name"));
            }
            jTextField53.setText(rs.getString("item_name"));            
            jTextField54.setText(rs.getString("item_currqty"));           
          }
         }catch(Exception e){System.out.println("edit item"+e);}
       }
       master_item_edit.setVisible(true);
        // TODO add your handling code here:
    }//GEN-LAST:event_edit8ActionPerformed

