    public void showreceipe()
    {
      try{          
          ResultSet rs=st.executeQuery("select * from reciepe;");
          DefaultTableModel dm=(DefaultTableModel) jTable5.getModel();
          int rc= dm.getRowCount();
            for(int i = 0;i<rc;i++){
                dm.removeRow(0);
            }   
          int j=0;
          key="";
          while(rs.next())
          { 
            j++;
            key+=rs.getString("reciepe_id")+"%";
            dm.addRow(new Object[]{j,rs.getString("reciepe_name")});                                                  
          }
         jTable5.setModel(dm);
       }catch(Exception e){System.out.println("jtable reciepe data 1st time:"+e);}
    }    
    public void validation(String err,String type)
    {
         if(type.equals("error"))
         {    
          JOptionPane.showMessageDialog(this,err,"Please Correct Following Fields",JOptionPane.ERROR_MESSAGE);        
         }
         if(type.equals("information"))
         {    
          JOptionPane.showMessageDialog(this,err,"",JOptionPane.INFORMATION_MESSAGE);        
         }             
    }
public void generateexcel(JTable tab,File file)
{
    try {

            WritableWorkbook workbook1 = Workbook.createWorkbook(file);
            WritableSheet sheet1 = workbook1.createSheet("First Sheet", 0);
            TableModel model = tab.getModel();
            WritableFont cellFont = new WritableFont(WritableFont.TIMES,14);
            WritableCellFormat cellFormat = new WritableCellFormat(cellFont);
            cellFormat.setBackground(jxl.format.Colour.GRAY_25);
            cellFormat.setWrap(true);
            WritableFont cellFont1 = new WritableFont(WritableFont.TIMES,12);
            WritableCellFormat cellFormat1 = new WritableCellFormat(cellFont1);
            cellFormat1.setBackground(jxl.format.Colour.ICE_BLUE);
            cellFormat1.setWrap(true);
            WritableFont cellFont2 = new WritableFont(WritableFont.TIMES,12);
            WritableCellFormat cellFormat2 = new WritableCellFormat(cellFont2);
            cellFormat2.setBackground(jxl.format.Colour.WHITE);
            cellFormat2.setWrap(true);
            for (int i = 0; i < model.getColumnCount(); i++) 
            {
                Label column = new Label(i, 0, model.getColumnName(i),cellFormat);
                sheet1.addCell(column);
                sheet1.setColumnView(i,22);
                
            }
            sheet1.setHeader("this is header1", "this is header2", "this is header3");
            int j = 0;
            for (int i = 0; i < model.getRowCount(); i++) {
                
                for (j = 0; j < model.getColumnCount(); j++) {
                    Label row;
                    if(i%2==0)
                    {
                     row = new Label(j, i + 1,
                            model.getValueAt(i, j).toString(),cellFormat2);
                    }
                    else
                    {
                         row = new Label(j, i + 1,
                            model.getValueAt(i, j).toString(),cellFormat1);
                    }
                    sheet1.addCell(row);
                }
            }
            workbook1.write();
            
            workbook1.close();
             Runtime rn = Runtime.getRuntime();
             String path = file.getPath();
             String str = "cmd.exe /c start "+path;
                    rn.exec(str);
                    JOptionPane.showMessageDialog(null, "Data saved at " +
                            path+"successfully", "Message",
                            JOptionPane.INFORMATION_MESSAGE);
            
        } catch (Exception ex) {
            ex.printStackTrace();
        }
}
     

